import re, base64

blob = ""   # paste your long string here

parts = re.split(r'0{4,}', blob)
tokens = [p.strip('0') for p in parts if re.search(r'[A-Za-z]', p)]

decoded = []
for tok in tokens:
    m = re.match(r'(\d+)([A-Za-z0-9+/=]+)', tok)
    seq, b64 = m.group(1), m.group(2)
    decoded.append(base64.b64decode(b64).decode('utf-8'))

# decoded is in appearance order; rotate to start at fragment with 'ctf{'
i = next(i for i,v in enumerate(decoded) if 'ctf{' in v)
rot = decoded[i:] + decoded[:i]
result = "".join(rot)
print(result)
